import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Search.css';

const Search = () => {

    const [searchValue, setSearchValue] = useState<string>()
    const [breeds, setBreeds] = useState<string[]>()
    const [image, setImage] = useState<string>(" ")
    const [status, setStatus] = useState<Boolean>(false)

    useEffect(() => {
        console.log(searchValue)
    }, [searchValue])

    const handleSearch = () => {
        console.log(searchValue)
        axios.get('https://dog.ceo/api/breeds/list/all')
        .then(res => {
            let breedsArr:string[] = []
            console.log(res.data.message)
            for(var i in res.data.message){
                if(Boolean(res.data.message[i][0])){
                    for(var j in res.data.message[i]){
                        breedsArr.push(`${i} ${res.data.message[i][j]}`)
                    }
                }
            }
            if(breedsArr && searchValue){
                const found:any = breedsArr.find(breed => breed.includes(searchValue))
                console.log(Boolean(found))
                if(found){
                    var breed = found.split(" ")[0]
                    var subBreed = found.split(" ")[1]
                    axios.get(`https://dog.ceo/api/breed/${breed}/${subBreed}/images/random`)
                    .then(res => {
                        setStatus(true)
                        setImage(res.data.message)
                    })
                    .catch(err => {
                        setStatus(false)
                        console.log(err)
                    })
                }else{
                    setStatus(false)
                }
            }
        })
        .catch(err => {
            console.log(err)
        })
    }

    const renderImage = () => {
        if(status){
            return <img src={image} />
        }else if(!status && image){
            return <span>Podaj nazwę psa</span>
        }
    }

    // useEffect(() => {
    //     console.log(Boolean(image))
    // }, [image])

    return (
        <div className='search-wrapper'>
            <label htmlFor="search-input">
                Podaj nazwę psa: 
            </label>
            <input type='text' id='search-input' onChange={(e:any)=>{setSearchValue(e.target.value)}}></input>
            <button className='search-btn' onClick={handleSearch}>Wyszukaj</button>
            {
                renderImage()
            }
        </div>
    );
}

export default Search;
